# Ordenar os pontos com base nos valores de X em ordem crescente
sorted_indices = np.argsort(points["X"])
sorted_X = np.array(points["X"])[sorted_indices]
sorted_Y = np.array(points["Y"])[sorted_indices]

# Criar o gráfico de dispersão com os pontos conectados em ordem crescente de X
plt.figure(figsize=(10, 6))
plt.scatter(sorted_X, sorted_Y, color='blue')
plt.plot(sorted_X, sorted_Y, color='red', linestyle='-', marker='o')  # Conectar os pontos

log = True

if log:
    #Definir a escala logarítmica nos eixos X e Y
    plt.xscale('log')
    plt.yscale('log')

    # Títulos e rótulos
    plt.title('Gráfico de Dispersão com Pontos Conectados em Ordem Crescente de X (Escala Logarítmica)', fontsize=14)
    plt.xlabel('Tamanho (escala logarítmica)', fontsize=12)
    plt.ylabel('Tempo (escala logarítmica)', fontsize=12)
else:
    plt.title('Gráfico de Dispersão com Pontos Conectados em Ordem Crescente de X', fontsize=14)
    plt.xlabel('Tamanho', fontsize=12)
    plt.ylabel('Tempo', fontsize=12)

# Exibir o gráfico
plt.grid(True, which="both", ls="--")
plt.show()